window.addEventListener("DOMContentLoaded",()=>{const urlParams=new URLSearchParams(window.location.search),newsId=urlParams.get("date");fetch("../data.json").then(response=>response.json()).then(newsDetail=>{const newsTitleElement=document.getElementById("news-title"),newsVideoElement=document.getElementById("news-video"),newsContentElement=document.getElementById("news-content");newsDetail=newsDetail[newsId],newsTitleElement.textContent=newsDetail.title,newsVideoElement.src=newsDetail.video,newsContentElement.innerHTML=newsDetail.content}).catch(error=>console.error("Error loading news detail:",error))});